     export const db = new DatabaseConnection();

    import { db } from ''@utils/database'';
     
    export async function UsersLayout() {
      let users = await db.query();
      // ...
    }

    import { db } from ''@utils/database'';
     
    export async function DashboardPage() {
      let user = await db.query();
      // ...
    }

